import React, { useState, Fragment, useEffect } from 'react';
import mqtt from "precompiled-mqtt";
import { MapContainer, TileLayer } from 'react-leaflet';
import './Seguimiento.css'


function Seguimiento() {
  const URL = "ws://192.168.0.104:9001";
  const client = mqtt.connect(URL);
  const [res, setRes] = useState({})
  var respuesta = {};

  useEffect(() => {
    conectar()
  },[""]);

  const conectar = () => {
    client.on('connect', () => {
      console.log("CONNECTED to broker");
    });
    client.subscribe("/temperaturaA");
    client.subscribe("/chat");
  }

  client.on('message', function (topic, message) {
    if (topic == "/temperaturaA") {
      respuesta = JSON.parse(message.toString());
      setRes(respuesta);
    } else if (topic == "chat") {
      var note = "Chunchito: " + "<div className='mensajeI'>" + message.toString() + "</div>";
      try {
        var aux = document.getElementById("mensajes").innerHTML
        document.getElementById("mensajes").innerHTML = aux + note
      } catch (error) { }
    }
  });

  // client.on('message', function (topic, message) {
  //   respuesta= JSON.parse(message.toString());
  //   setRes(respuesta);
  //   var note = "Chunchito: "+"<div className='mensajeI'>"+ message.toString()+"</div>";
  //   try {
  //     document.getElementById("mensajes").innerHTML=document.getElementById("mensajes").innerHTML+ note
  //   } catch (error) {

  //   }
  // });

  function enviar() {
    var enviado = "Yo: " + document.getElementById("txtIn").value;
    client.publish("World", enviado);
  }

  return (
    <div>
      <div className='mapa'>
        <div className="leaflet-map">
          < MapContainer className='leaflet-map' center={[19.494102, -96.91589]} zoom={13} scrollWheelZoom={false} >
            <TileLayer
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            />
            {/* <Marker position={[18.494102, 73.932754]}>
          <Popup>Tadaa! Here's where I live</Popup>
          </Marker> */}
            <div className="map-content">
              <span>Mapa de seguimiento</span>
            </div>
          </MapContainer >
        </div >
        <div className='chat'>
          <div id='mensajes' className='mensajes'>

          </div>
          <div className='barraTxt'>
            <input id='txtIn' className='inputMsg' placeholder='Ecribe tu mensaje'></input>
            <button className='btnEnviar' onClick={enviar} >Enviar</button>
          </div>
        </div>
      </div>
      <div className='datos'>
        <div className='datosI'>
          Temperatura
          <h3>{res.temperatura}</h3>
        </div>
        <div className='datosI'>
          Humedad
          <h3>80%</h3>
        </div>
        <div className='datosI'>
          Latitud
          <h3>19.53124</h3>
        </div>
        <div className='datosI'>
          Longitud
          <h3>-96.91589</h3>
        </div>
      </div>
    </div>
  );
}

export default Seguimiento;
