import React, { useState, Fragment, useEffect } from 'react';
import mqtt from "precompiled-mqtt";


function Seguimiento() {
  const URL = "ws://192.168.0.105:9001";

  const client = mqtt.connect(URL);

  client.on('connect', () => {
    console.log("CONNECTED to roker");
  });

  client.subscribe("/temperaturaA");
  
  client.on('message', function (topic, message) {
    note = message.toString();
    // Updates React state with message 
    setMesg(note);
    console.log(note);
    client.end();
    });
  
  useEffect(() => {
    onMessage()
  });
  
  return (
    <div className="App">
      <header className="App-header">
        <h1>A taste of MQTT in React</h1>
        <p>The message is: mesg</p>
        <p>
          <a href="https://www.preciouschicken.com/blog/posts/a-taste-of-mqtt-in-react/"
            style={{
              color: 'white'
            }}>preciouschicken.com/blog/posts/a-taste-of-mqtt-in-react/</a>
        </p>
      </header>
    </div>
  );
}

export default Seguimiento;
