import React, { useState, Fragment } from 'react';

import mqtt from "precompiled-mqtt";

// mosquitto test broker
//const URL = "mqtt://test.mosquitto.org:8081";

// Local broker

function Seguimiento() {
  const URL = "ws://192.168.0.105:9001";

const client = mqtt.connect(URL);

client.on('connect', () => {
    console.log("CONNECTED to broker");
});


const onMessage = (callBack) => {
    client.on("message", (topic, message, packet) => {
        callBack(JSON.parse(new TextDecoder("utf-8").decode(message)));
    });
}


  return (
    <div className="App">
    <header className="App-header">
    <h1>A taste of MQTT in React</h1>
    <p>The message is: mesg</p>
		<p>
		<a href="https://www.preciouschicken.com/blog/posts/a-taste-of-mqtt-in-react/"    
		style={{
			color: 'white'
		}}>preciouschicken.com/blog/posts/a-taste-of-mqtt-in-react/</a>
		</p>
		</header>
		</div>
  );
}

export default Seguimiento;
