import React, { useState } from 'react';
import { useNavigate } from "react-router-dom";
import './Camiones.css';
import { Modal } from 'react-bootstrap';

const ActualizarModal = () => {
    const history = useNavigate();

    const [show, setShow] = useState(false);
    const [score, setScore] = useState(0)

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    const indexStart = (index) => {
        setScore(index + 1)
    }


    return (
        <>
            <button  onClick={handleShow}>
                Calificar  â˜…
            </button>

            <Modal show={show} onHide={handleClose}>
                <Modal.Header closeButton>
                    <Modal.Title>Califica este juego </Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <h5>Indica la cantidad de estrellas que se merece</h5>
                    <div>
                        {
                            /*eslint rest-spread-spacing: ["error", "never"]*/
                            [...new Array(5)].map((star, index) => {
                                return index < score ? <label key={index} className='iconStar' onClick={() => indexStart(index)} /> : <label key={index} className='iconStar' onClick={() => indexStart(index)} />
                            })
                        }
                    </div>
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="primary" >
                        Guardar calificacion
                    </Button>
                    <Button variant="secondary" type='submit' onClick={handleClose}>
                        Cerrar
                    </Button>
                </Modal.Footer>
            </Modal>
        </>
    );
}

export default ActualizarModal;
