import { useEffect, useState } from 'react';
import * as CamionesServer from '../Camiones/CamionesServer'
import './Facturas.css';


function Facturas() {

    const [camiones, setCamiones] = useState([]);
    const [camion, setCamion] = useState({ chofer: "", temperatura: [], objeto: "", cantidad: 0, latitud: 0, longitud: 0 });
    const [factura, setFactura] = useState({ chofer: "", idCamion: 0, objeto: "", receptor: 0, fecha: 0, hora: 0 });

    const handleInputChange = (e) => {
        setFactura({ ...factura, [e.target.name]: e.target.value });
    };

    const listaCamiones = async () =>{
        try{
            const data = await (await CamionesServer.getAllCamiones()).json();
            setCamiones(data.data);
            console.log(data.data)
            for (let index = 0; index < data.data.length; index++) {
                document.getElementById("idCamion").innerHTML= document.getElementById("idCamion").innerHTML + "<option>"+data.data[index].id+"</option>" ;
            }
        }catch(error){
            console.log(error);
        }
    };

    const handleChangeSelected= async()=>{
        const data = await (await CamionesServer.getCamionById(document.getElementById("idCamion").value)).json();
        console.log(data)
        const { chofer, temperatura, objeto,cantidad,latitud,longitud } = data.data;
        setCamion({ chofer, temperatura, objeto,cantidad,latitud,longitud });
    }
    
    useEffect(() => {
        listaCamiones();
        //optSelect();
        // eslint-disable-next-line
    },[]);

    return (
        <div className="general">
            <h1>Generar facturas</h1>
            <div>
                <form className="form" /*onSubmit={handleSubmit}*/>
                    <div className='divB'>
                        <label className='label'>Camion</label>
                        <select name='idCamion' id='idCamion' className="input" type="number" step="any" onChange={handleChangeSelected} >
                        </select>
                        <input name='chofer' id='chofer' className="input" placeholder="Nombre chofer" onChange={handleInputChange} value={camion.chofer} ></input>
                        <input name='objeto' id='objeto' className="input" placeholder="Carga" onChange={handleInputChange} value={camion.objeto} ></input>
                    </div>
                    <div className='divB'>
                        <label className='label'>Recepci√≥n</label>
                        <input name='receptor' id='receptor' className="input" placeholder="Receptor" /*onChange={handleInputChange}*/ ></input>
                        <input name='fecha' id='fecha' className="input" type="date" step="any" placeholder="Fecha" /*onChange={handleInputChange}*/ ></input>
                        <input name='hora' id='hora' className="input" type="time" step="any" placeholder="Hora" /*onChange={handleInputChange}*/ ></input>
                    </div>
                    <button type='submit' className='btn'>Agregar</button>
                </form>
            </div>
            <div className="divCard">
            </div>
        </div>
    );
}

export default Facturas;
