import React, { useEffect } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import './Seguimiento.css';
import Paho from 'paho-mqtt';

const Seguimiento = () => {

  function onConnectionLost(responseObject) {
    if (responseObject.errorCode !== 0) {
        console.log("onConnectionLost:" + responseObject.errorMessage);
    }
}

// called when a message arrives
function onMessageArrived(message) {
    var mensajeJson = JSON.parse(message.payloadString);
    puntito.setLatLng(L.latLng(mensajeJson.c1, mensajeJson.c2))
    puntito.bindPopup("<b> Temperatura: " + mensajeJson.temperatura + "</b>").openPopup();

}

  useEffect(() => {
    const client = new Paho.MQTT.Client("192.168.0.105", 9001, "CatBenderson2");
    client.onConnectionLost = onConnectionLost;
    client.onMessageArrived = onMessageArrived;
  }, []);

  return (
    <div>
      <div className='mapa'>
        <div className="leaflet-map">
          < MapContainer className='leaflet-map' center={[19.494102, -96.91589]} zoom={13} scrollWheelZoom={false} >
            <TileLayer
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            />
            {/* <Marker position={[18.494102, 73.932754]}>
          <Popup>Tadaa! Here's where I live</Popup>
          </Marker> */}
            <div className="map-content">
              <span>Mapa de seguimiento</span>
            </div>
          </MapContainer >
        </div >
        <div className='chat'>
          <div className='mensajes'>
            ñam
          </div>
          <div className='barraTxt'>
            <input className='inputMsg' placeholder='Ecribe tu mensaje'></input>
            <button className='btnEnviar' >Enviar</button>
          </div>
        </div>
      </div>
      <div className='datos'>
        <div className='datosI'>
          Temperatura
          <h1>24°</h1>
        </div>
        <div className='datosI'>
          Humedad
          <h1>80%</h1>
        </div>
        <div className='datosI'>
          Latitud
          <h1>19.53124</h1>
        </div>
        <div className='datosI'>
          Longitud
          <h1>-96.91589</h1>
        </div>
      </div>
    </div>
  )
}

export default Seguimiento
