import { useState } from 'react';
import './Camiones.css';
import CamionesList from './CamionesList';

function agregarCamion(){
  var nombre=document.getElementById;
}

function Camiones() {
  const [camion, setCamion] = useState({ chofer: "", temperatura: 0.0, objeto:"",cantidad:0,latitud:0.0,longitud:0.0});

  const handleInputChange = (e) => {
    setCamion({ ...camion, [e.target.name]: e.target.value });
};

  return (
    <div className="general">
      <h1>Administración de camiones</h1>
      <div>
        <form className="form">
          <div className='divB'>
            <label className='label'>Camion</label>
            <input id='chofer' className="input" placeholder="Nombre chofer" onChange={handleInputChange} ></input>
            <input id='temperatura' className="input" placeholder="Temperatura"onChange={handleInputChange} ></input>
          </div>
          <div className='divB'>
            <label className='label'>Carga</label>
            <input id='objeto' className="input" placeholder="Carga"></input>
            <input id='cantidad' type='number' className="input" placeholder="Cantidad"></input>
          </div>
          <div className='divB'>
            <label className='label'>Ubicación</label>
            <input id='latitud' className="input" placeholder="Latitud"></input>
            <input id='longitud' className="input" placeholder="Longitud"></input>
          </div>
          <button className='btn'>Agregar</button>
        </form>
      </div>
      <div className="divCard">
        {/* <div className="card">
          <h2 className="h5"><strong>1</strong></h2>
          <img className='img' src={Camion} />
          <h5 className="h5">Ame Huerta Mendoza</h5>
          <h5 className="h5">250 Manzanas</h5>
          <h5 className="h5">-19.2345666,  19.823456</h5>
          <button className="btnC">Actualizar</button>
          <button className="btnC">Eliminar</button>
        </div> */}
          <CamionesList/>
      </div>
    </div>
  );
}

export default Camiones;
