import React, { useState, Fragment } from "react";
import "./App.css";
 
var mqtt = require("mqtt");
var options = {
  protocol: "ws",
  username: "xxxxxxxxx",
  password: "xxxxxxxx",
  keepalive: 20,
  // clientId uniquely identifies client
  // choose any string you wish
  clientId: "mqttjs_" + Math.random().toString(16).substr(2, 8),
};
var client = mqtt.connect("mqtt://<your IP address>:<your websocket port>", options);
 
client.subscribe("publishtopic");
console.log("Client subscribed ");
 
function Seguimiento() {
  var note;
  client.on("message", function (topic, message) {
    note = message.toString();
    // Updates React state with message
    setMsg(note);
    console.log(note);
    client.end();
  });
 
  // Sets default React state
  const [msg, setMsg] = useState(
    <Fragment>
      <em>...</em>
    </Fragment>
  );

  return (
    <div>
      <div className='mapa'>
        <div className="leaflet-map">
          < MapContainer className='leaflet-map' center={[19.494102, -96.91589]} zoom={13} scrollWheelZoom={false} >
            <TileLayer
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            />
            {/* <Marker position={[18.494102, 73.932754]}>
          <Popup>Tadaa! Here's where I live</Popup>
          </Marker> */}
            <div className="map-content">
              <span>Mapa de seguimiento</span>
            </div>
          </MapContainer >
        </div >
        <div className='chat'>
          <div className='mensajes'>
            ñam
          </div>
          <div className='barraTxt'>
            <input className='inputMsg' placeholder='Ecribe tu mensaje'></input>
            <button className='btnEnviar' >Enviar</button>
          </div>
        </div>
      </div>
      <div className='datos'>
        <div className='datosI'>
          Temperatura
          <h1>24°</h1>
        </div>
        <div className='datosI'>
          Humedad
          <h1>80%</h1>
        </div>
        <div className='datosI'>
          Latitud
          <h1>19.53124</h1>
        </div>
        <div className='datosI'>
          Longitud
          <h1>-96.91589</h1>
        </div>
      </div>
    </div>
  )
}

export default Seguimiento
