import React, { useEffect } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import './Seguimiento.css';
import * as mqtt from 'react-paho-mqtt';

const Seguimiento = () => {
  const [client, setClient] = useState(null);
  const topic = ["Hello"];
  const options = {};

  useEffect(() => {
    conectar();
  },[])

  const conectar = () => {
    const c = mqtt.connect("192.168.0.105", "8081", "CatBenderson2", onConnectionLost, onMessageArrived);
    setClient(c);
  }

  const _sendPayload = () => {
    const payload = mqtt.parsePayload("Hello", "World"); // topic, payload
    client.send(payload);
  }

  const onConnectionLost = responseObject => {
    if (responseObject.errorCode !== 0) {
      console.log("onConnectionLost: " + responseObject.errorMessage);
    }
  }

  const onMessageArrived = message => {
    console.log("onMessageArrived: " + message.payloadString);
  }

  const _onSubscribe = () => {
    client.connect({ onSuccess: () => {
      for (var i = 0; i < _topic.length; i++) {
        client.subscribe(_topic[i], _options);
      }}
    });
  }

  const _onUnsubscribe = () => {
    for (var i = 0; i < _topic.length; i++) {
      client.unsubscribe(_topic[i], _options);
    }
  }

  const _onDisconnect = () => {
    client.disconnect();
  }

  return (
    <div>
      <div className='mapa'>
        <div className="leaflet-map">
          < MapContainer className='leaflet-map' center={[19.494102, -96.91589]} zoom={13} scrollWheelZoom={false} >
            <TileLayer
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            />
            {/* <Marker position={[18.494102, 73.932754]}>
          <Popup>Tadaa! Here's where I live</Popup>
          </Marker> */}
            <div className="map-content">
              <span>Mapa de seguimiento</span>
            </div>
          </MapContainer >
        </div >
        <div className='chat'>
          <div className='mensajes'>
            Ã±am
          </div>
          <div className='barraTxt'>
            <input className='inputMsg' placeholder='Ecribe tu mensaje'></input>
            <button className='btnEnviar' >Enviar</button>
          </div>
        </div>
      </div>
      <div className='datos'>
        <div className='datosI'>
          Temperatura
          <h1>24Â°</h1>
        </div>
        <div className='datosI'>
          Humedad
          <h1>80%</h1>
        </div>
        <div className='datosI'>
          Latitud
          <h1>19.53124</h1>
        </div>
        <div className='datosI'>
          Longitud
          <h1>-96.91589</h1>
        </div>
      </div>
    </div>
  )
}

export default Seguimiento
