import { useEffect, useState } from 'react';
import * as CamionesServer from '../Camiones/CamionesServer'
import './Facturas.css';


function Facturas() {

    const [camiones, setCamiones] = useState([]);

    const listaCamiones = async () =>{
        try{
            const data = await (await CamionesServer.getAllCamiones()).json();
            setCamiones(data.data);
            console.log(data.data)
        }catch(error){
            console.log(error);
        }
    };

    function optSelect(){
        var aux= document.getElementById("idCamion").innerHTML;
        
    }

    useEffect(() => {
        listaCamiones();
        optSelect();
        // eslint-disable-next-line
    },[]);

    return (
        <div className="general">
            <h1>Generar facturas</h1>
            <div>
                <form className="form" /*onSubmit={handleSubmit}*/>
                    <div className='divB'>
                        <label className='label'>Camion</label>
                        <select name='idCamion' id='idCamion' className="input" type="number" step="any" /*onChange={handleInputChange}*/ >
                            <option>{camiones.length}</option>
                        </select>
                        <input name='chofer' id='chofer' className="input" placeholder="Nombre chofer" /*onChange={handleInputChange}*/ ></input>
                        <input name='objeto' id='objeto' className="input" placeholder="Carga" /*onChange={handleInputChange}*/ ></input>
                    </div>
                    <div className='divB'>
                        <label className='label'>Recepci√≥n</label>
                        <input name='receptor' id='receptor' className="input" placeholder="Receptor" /*onChange={handleInputChange}*/ ></input>
                        <input name='fecha' id='fecha' className="input" type="date" step="any" placeholder="Fecha" /*onChange={handleInputChange}*/ ></input>
                        <input name='hora' id='hora' className="input" type="time" step="any" placeholder="Hora" /*onChange={handleInputChange}*/ ></input>
                    </div>
                    <button type='submit' className='btn'>Agregar</button>
                </form>
            </div>
            <div className="divCard">
            </div>
        </div>
    );
}

export default Facturas;
