import React, { useState, Fragment, useEffect } from 'react';
import mqtt from "precompiled-mqtt";
import { MapContainer, TileLayer } from 'react-leaflet';
import './Seguimiento.css'


function Seguimiento() {
  const [note,setNote]=useState("")
  const URL = "ws://192.168.0.105:9001";

  const client = mqtt.connect(URL);

  client.on('connect', () => {
    console.log("CONNECTED to roker");
  });

  client.subscribe("/temperaturaA");
  
  client.on('message', function (topic, message) {
    setNote(message.toString());
    try {
      document.getElementById("mensajes").innerHTML=document.getElementById("mensajes").innerHTML+ "<label>"+note+"</label>"
    } catch (error) {
      
    }
    });
  
  useEffect(() => {
  });
  
  return (
    <div>
      <div className='mapa'>
        <div className="leaflet-map">
          < MapContainer className='leaflet-map' center={[19.494102, -96.91589]} zoom={13} scrollWheelZoom={false} >
            <TileLayer
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            />
            {/* <Marker position={[18.494102, 73.932754]}>
          <Popup>Tadaa! Here's where I live</Popup>
          </Marker> */}
            <div className="map-content">
              <span>Mapa de seguimiento</span>
            </div>
          </MapContainer >
        </div >
        <div className='chat'>
          <div id="mensajes" className='mensajes'>
            
          </div>
          <div className='barraTxt'>
            <input className='inputMsg' placeholder='Ecribe tu mensaje'></input>
            <button className='btnEnviar' >Enviar</button>
          </div>
        </div>
      </div>
      <div className='datos'>
        <div className='datosI'>
          Temperatura
          <h1>24Â°</h1>
        </div>
        <div className='datosI'>
          Humedad
          <h1>80%</h1>
        </div>
        <div className='datosI'>
          Latitud
          <h1>19.53124</h1>
        </div>
        <div className='datosI'>
          Longitud
          <h1>-96.91589</h1>
        </div>
      </div>
    </div>
  );
}

export default Seguimiento;
